var $ = require('jquery');

module.exports = {
    isMobile: function() {
        return ($(document).width() <= 600);
    },
    // Breakpoint for navigation links position
    isSmallScreen: function() {
        return ($(document).width() <= 1240);
    },
    // 是否是销客app内web环境
    isFSBrowser: function() {
        return /FSBrowser\/((\d+.?)+)/i.test(window.navigator.userAgent);
    },
    isWXBrowser: function() {
        return /MicroMessenger|MQQBrowser/i.test(window.navigator.userAgent);
    }
};
